            if (annotation.id === self.state.id) {
                self.setState({
                    showMoreFlag: true,
                    highlight: true
                });
                location.hash = "#/pdf/page/" + self.state.pageNo + "/annotation/" + self.state.id;
            } else {
                self.setState({
                    showMoreFlag: false,
                    highlight: false
                });
            }
